#To install, please run these two commands in sequence. The first command requires SUDO and will will restart your computer. The second command is benign.
cd ~/ && sudo apt full-upgrade && sudo apt install libgnutls28-dev openssl qtbase5-dev libqt5core5a libqt5widgets5 meson cmakepython3-yaml libglib2.0-dev libgstreamer-plugins-base1.0-dev python3-ply libtiff-dev pybind11-dev python3-pip libboost-dev python3-jinja2 git pipx gcc gcc-aarch64-linux-gnu libcap-dev python3-dev python3-tk && git clone https://github.com/sp29174/GreenhousePython.git && git clone https://git.libcamera.org/libcamera/libcamera.git && pipx ensurepath && reboot
cd ~/ && cd libcamera && meson setup build --buildtype=release -Dgstreamer=enabled -Dpycamera=enabled && ninja -j 1 -vC build install && cd .. && pipx install -vvv poetry && poetry completions bash >> ~/.bash_completion && cd ./GreenhousePython && eval $(poetry env activate) && poetry install -vvv --all-groups --all-extras --compile && poetry run -vvv python -vvi ./src/main.py 
#To uninstall, please run this command:
cd ~/ && poetry env remove --all && pipx uninstall-all -vvv && rm -rf ./GreenhousePython && sudo apt purge git pipx gcc gcc-aarch64-linux-gnu libcap-dev python3-dev && sudo apt full-upgrade && reboot
